mapper.select:
		SELECT * FROM(
            SELECT
                rownum AS no,
                id,
                email,
                password,
                role,
                user_id,
                office_code,
                FN_GET_OFFICE_NAME(office_code) as office_name,
                user_name,
                tel,
                address,
                address_detail,
                postal_code,
                uuid,
                system_update_userid,
                system_update_date,
                system_create_userid,
                system_create_date
		    FROM COMMON_USER

                1=1

                AND id=#{id}
                AND email=#{email}
                AND password=#{password}
                AND role=#{role}
                AND user_id=#{user_id}
                AND office_code=#{office_code}
                AND user_name=#{user_name}
                AND tel=#{tel}
                AND address=#{address}
                AND address_detail=#{address_detail}
                AND postal_code=#{postal_code}
                AND uuid=#{uuid}


                        AND TO_CHAR(system_create_date, 'yyyyMMdd') BETWEEN #{from_date} AND '99999999'


                        AND TO_CHAR(system_create_date, 'yyyyMMdd') BETWEEN '00000000' AND #{to_date}


                        AND TO_CHAR(system_create_date,'yyyyMMdd') BETWEEN #{from_date} AND #{to_date}


                AND id like '%'||#{_id}||'%'
                AND email like '%'||#{_email}||'%'
                AND password like '%'||#{_password}||'%'
                AND role like '%'||#{_role}||'%'
                AND user_id like '%'||#{_user_id}||'%'
                AND user_name like '%'||#{_user_name}||'%'
                AND office_code like '%'||#{_office_code}||'%'

	    )

            no BETWEEN ((#{page_no} -1) * #{row_range} + 1) AND (#{row_range} * #{page_no})

SELECT CEIL(COUNT(*)/#{row_range}) FROM COMMON_USER

    1=1

    AND id=#{id}
    AND email=#{email}
    AND password=#{password}
    AND role=#{role}
    AND user_id=#{user_id}
    AND office_code=#{office_code}
    AND user_name=#{user_name}
    AND tel=#{tel}
    AND address=#{address}
    AND address_detail=#{address_detail}
    AND postal_code=#{postal_code}
    AND uuid=#{uuid}


            AND TO_CHAR(system_create_date, 'yyyyMMdd') BETWEEN #{from_date} AND '99999999'


            AND TO_CHAR(system_create_date, 'yyyyMMdd') BETWEEN '00000000' AND #{to_date}


            AND TO_CHAR(system_create_date,'yyyyMMdd') BETWEEN #{from_date} AND #{to_date}

SELECT * FROM(
SELECT
rownum AS no,
id,
email,
password,
role,
user_id,
office_code,
FN_GET_OFFICE_NAME(office_code) as office_name,
user_name,
tel,
address,
address_detail,
postal_code,
uuid,
system_update_userid,
system_update_date,
system_create_userid,
system_create_date
FROM COMMON_USER

    1=1

    AND id=#{id}
    AND not EXISTS(select * from INSPECTION_STATION_INSPECTOR where inspection_station_id = office_code and inspector_group_id =#{inspector_group_id})
    AND email=#{email}
    AND password=#{password}
    AND role=#{role}
    AND user_id=#{user_id}
    AND office_code=#{office_code}
    AND user_name=#{user_name}
    AND tel=#{tel}
    AND address=#{address}
    AND address_detail=#{address_detail}
    AND postal_code=#{postal_code}
    AND uuid=#{uuid}


            AND TO_CHAR(system_create_date, 'yyyyMMdd') BETWEEN #{from_date} AND '99999999'


            AND TO_CHAR(system_create_date, 'yyyyMMdd') BETWEEN '00000000' AND #{to_date}


            AND TO_CHAR(system_create_date,'yyyyMMdd') BETWEEN #{from_date} AND #{to_date}


    AND id like '%'||#{_id}||'%'
    AND email like '%'||#{_email}||'%'
    AND password like '%'||#{_password}||'%'
    AND role like '%'||#{_role}||'%'
    AND user_id like '%'||#{_user_id}||'%'
    AND user_name like '%'||#{_user_name}||'%'
    AND office_code like '%'||#{_office_code}||'%'

)

    no BETWEEN ((#{page_no} -1) * #{row_range} + 1) AND (#{row_range} * #{page_no})

