SELECT
    rownum AS no,

    id,
    email,
    password,
    role,
    user_id,
    office_code,
    FN_GET_OFFICE_NAME(office_code) AS office_name,
    FN_GET_OFFICE_TYPE(office_code) AS office_type,
    user_name,
    tel,
    address,
    address_detail,
    postal_code,
    uuid,
    system_update_userid,
    system_update_date,
    system_create_userid,
    system_create_date
FROM COMMON_USER

    1=1
    AND id=#{id}
    AND email=#{email}
    AND password=#{password}
    AND role=#{role}
    AND user_id=#{user_id}
    AND office_code=#{office_code}
    AND user_name=#{user_name}
    AND tel=#{tel}
    AND address=#{address}
    AND address_detail=#{address_detail}
    AND postal_code=#{postal_code}
    AND uuid=#{uuid}

mapper.insert:
INSERT INTO COMMON_USER(
	system_create_date
	, system_update_date 
    , id  
		, email
		, password
		, role
		, user_id
		, office_code
		, user_name
		, tel
		, address
		, address_detail
		, postal_code
		, uuid

)
VALUES(
	systimestamp
	, systimestamp 
	, #{id}   
		, #{email}
		, #{password}
		, #{role}
		, #{user_id}
		, #{office_code}
		, #{user_name}
		, #{tel}
		, #{address}
		, #{address_detail}
		, #{postal_code}
		, #{uuid}
)

mapper.delete:
		DELETE FROM COMMON_USER

		    AND id = #{id}  
            AND email = #{email}
            AND user_id = #{user_id}

mapper.update:
		UPDATE  COMMON_USER

				system_update_date = systimestamp 
                , system_update_userid=#{system_update_userid}
				, email=#{email}
				, password=#{password}
				, role=#{role}
				, user_id=#{user_id}
				, office_code=#{office_code}
				, user_name=#{user_name}
				, tel=#{tel}
				, address=#{address}
				, address_detail=#{address_detail}
				, postal_code=#{postal_code}
				, uuid=#{uuid}


		    AND id = #{id}
            AND email = #{email}
            AND user_id = #{user_id}

mapper.select:
SELECT * FROM(
    SELECT
        rownum AS no,
        a.id,
        a.email,
        a.password,
        a.role,
        a.user_id,
        a.office_code
    FROM COMMON_USER a

    NOT EXISTS (
        SELECT 1
        FROM COMMON_USER_GROUP b
        WHERE a.user_id = b.user_id
        AND b.group_id = #{group_id}
    )
    AND a.id=#{id}
    AND a.email=#{email}
    AND a.password=#{password}
    AND a.role=#{role}
    AND a.user_id=#{user_id}
    AND a.office_code=#{office_code}


    AND a.id like '%'||#{_id}||'%'
    AND a.email like '%'||#{_email}||'%'
    AND a.password like '%'||#{_password}||'%'
    AND a.role like '%'||#{_role}||'%'
    AND a.user_id like '%'||#{_user_id}||'%'
    AND a.office_code like '%'||#{_office_code}||'%'

)

    no BETWEEN ((#{page_no} -1) * #{row_range} + 1) AND (#{row_range} * #{page_no})

SELECT CEIL(COUNT(*)/#{row_range}) FROM COMMON_USER a

    NOT EXISTS (
        SELECT 1
        FROM COMMON_USER_GROUP b
        WHERE a.user_id = b.user_id
        AND b.group_id = #{group_id}
    )
    AND a.id=#{id}
    AND a.email=#{email}
    AND a.password=#{password}
